import React, { useState } from 'react';
import Chatbot from './components/Chatbot';
import './App.css';

const App: React.FC = () => {
  const [isChatbotOpen, setIsChatbotOpen] = useState(true);

  // These would come from your app's state/context in production
  const bookingId = 'booking_123';
  const userId = 'user_456';
  const driverId = 'driver_789';
  const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:3001';

  const handleEscalation = (escalationType: string) => {
    console.log(`Chat escalated to: ${escalationType}`);
    // Handle escalation in your app (show different UI, notify support team, etc.)
  };

  const handleCloseChatbot = () => {
    setIsChatbotOpen(false);
    // You might want to show a "Chat" button instead
  };

  return (
    <div className="app">
      <div className="app-container">
        <h1>RideShare Pro</h1>
        <p>Your ride details and support chat</p>
      </div>

      {isChatbotOpen && (
        <div className="chatbot-wrapper">
          <Chatbot
            bookingId={bookingId}
            userId={userId}
            driverId={driverId}
            apiUrl={apiUrl}
            onEscalation={handleEscalation}
            onClose={handleCloseChatbot}
          />
        </div>
      )}

      {!isChatbotOpen && (
        <button className="open-chat-btn" onClick={() => setIsChatbotOpen(true)}>
          ðŸ’¬ Open Chat
        </button>
      )}
    </div>
  );
};

export default App;
