import React, { useEffect, useState, useRef } from 'react';
import ChatbotApiService from '../services/chatbotApi';
import { ChatbotProps, Message, QuickActionButton } from '../types';
import '../styles/chatbot.css';

const Chatbot: React.FC<ChatbotProps> = ({
  bookingId,
  userId,
  driverId,
  apiUrl,
  onEscalation,
  onClose,
}) => {
  const [conversationId, setConversationId] = useState<string | null>(null);
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputText, setInputText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [isEscalated, setIsEscalated] = useState(false);
  const [escalationType, setEscalationType] = useState<'driver' | 'support' | 'safety' | null>(null);
  const [quickActions, setQuickActions] = useState<QuickActionButton[]>([]);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const apiService = useRef(new ChatbotApiService(apiUrl));

  // Initialize chatbot on mount
  useEffect(() => {
    initializeChatbot();
  }, [bookingId, userId]);

  // Scroll to bottom when new messages arrive
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const initializeChatbot = async () => {
    try {
      setIsLoading(true);
      setError(null);

      const response = await apiService.current.initiateChatbot(bookingId, userId, driverId);

      setConversationId(response.conversationId);

      // Add greeting message
      const greetingMessage: Message = {
        id: `msg_${Date.now()}`,
        sender: 'bot',
        text: response.message,
        timestamp: new Date(),
      };

      setMessages([greetingMessage]);

      // Set quick actions
      const actions: QuickActionButton[] = (response.suggestedActions || []).map(
        (action: string) => ({
          id: action,
          label: formatActionLabel(action),
          action,
        })
      );
      setQuickActions(actions);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to initialize chatbot';
      setError(errorMessage);
      console.error('Chatbot initialization error:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const formatActionLabel = (action: string): string => {
    const labels: Record<string, string> = {
      where_is_driver: 'üìç Where is my driver?',
      driver_late: '‚è∞ Driver is late',
      contact_driver: 'üìû Contact driver',
      cannot_contact_driver: '‚ùå Cannot reach driver',
      cancel_booking: 'üö´ Cancel booking',
      talk_to_agent: 'üë§ Talk to agent',
      call_driver: 'üìû Call driver',
      message_driver: 'üí¨ Message driver',
      payment_query: 'üí≥ Payment question',
      safety_concern: '‚ö†Ô∏è Safety concern',
    };
    return labels[action] || action.replace(/_/g, ' ');
  };

  const handleSendMessage = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!inputText.trim() || !conversationId) {
      return;
    }

    // Add user message
    const messageText = inputText;
    
    const userMessage: Message = {
      id: `msg_${Date.now()}`,
      sender: 'user',
      text: messageText,
      timestamp: new Date(),
    };

    setMessages((prev) => [...prev, userMessage]);
    setInputText('');
    setIsLoading(true);
    setError(null);

    try {
      const response = await apiService.current.sendMessage(
        conversationId,
        bookingId,
        userId,
        messageText
      );

      // Add bot response
      const botMessage: Message = {
        id: `msg_${Date.now()}`,
        sender: 'bot',
        text: response.message,
        timestamp: new Date(),
        metadata: response.metadata,
      };

      setMessages((prev) => [...prev, botMessage]);

      // Update quick actions
      if (response.suggestedActions) {
        const actions: QuickActionButton[] = response.suggestedActions.map((action: string) => ({
          id: action,
          label: formatActionLabel(action),
          action,
        }));
        setQuickActions(actions);
      }

      // Handle escalation
      if (response.requiresEscalation) {
        setIsEscalated(true);
        setEscalationType(response.escalationType as 'driver' | 'support' | 'safety');

        if (onEscalation) {
          onEscalation(response.escalationType);
        }

        const escalationMessage: Message = {
          id: `msg_${Date.now()}`,
          sender: 'bot',
          text: `I'm escalating your issue to ${response.escalationType} support. Please stand by until they connect with you.`,
          timestamp: new Date(),
        };

        setTimeout(() => {
          setMessages((prev) => [...prev, escalationMessage]);
        }, 1000);
      }
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to send message';
      setError(errorMessage);
      console.error('Message send error:', err);

      const errorBotMessage: Message = {
        id: `msg_${Date.now()}`,
        sender: 'bot',
        text: 'Sorry, I encountered an error. Let me connect you with support.',
        timestamp: new Date(),
      };

      setMessages((prev) => [...prev, errorBotMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleQuickAction = async (action: string) => {
    if (!conversationId) {
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const response = await apiService.current.sendQuickAction(
        conversationId,
        bookingId,
        userId,
        action
      );

      // Add action as user message
      const userMessage: Message = {
        id: `msg_${Date.now()}`,
        sender: 'user',
        text: formatActionLabel(action),
        timestamp: new Date(),
      };

      setMessages((prev) => [...prev, userMessage]);

      // Add bot response
      const botMessage: Message = {
        id: `msg_${Date.now()}`,
        sender: 'bot',
        text: response.message,
        timestamp: new Date(),
        metadata: response.metadata,
      };

      setMessages((prev) => [...prev, botMessage]);

      // Update quick actions
      if (response.suggestedActions) {
        const actions: QuickActionButton[] = response.suggestedActions.map((a: string) => ({
          id: a,
          label: formatActionLabel(a),
          action: a,
        }));
        setQuickActions(actions);
      }

      // Handle escalation
      if (response.requiresEscalation) {
        setIsEscalated(true);
        setEscalationType(response.escalationType as 'driver' | 'support' | 'safety');

        if (onEscalation) {
          onEscalation(response.escalationType);
        }
      }
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to process action';
      setError(errorMessage);
      console.error('Quick action error:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleCloseChat = async () => {
    if (conversationId) {
      try {
        await apiService.current.closeConversation(conversationId, 'User closed chat');
      } catch (err) {
        console.error('Error closing conversation:', err);
      }
    }

    if (onClose) {
      onClose();
    }
  };

  return (
    <div className="chatbot-container">
      <div className="chatbot-header">
        <h3>RideShare Support</h3>
        <button className="close-btn" onClick={handleCloseChat} title="Close chat">
          ‚úï
        </button>
      </div>

      <div className="chatbot-messages">
        {messages.map((message) => (
          <div key={message.id} className={`message message-${message.sender}`}>
            <div className="message-content">
              {message.sender === 'bot' && <span className="message-avatar">ü§ñ</span>}
              {message.sender === 'user' && <span className="message-avatar">üë§</span>}
              {message.sender === 'driver' && <span className="message-avatar">üöó</span>}
              {message.sender === 'support_agent' && <span className="message-avatar">üë®‚Äçüíº</span>}
              <div className="message-text">{message.text}</div>
              <span className="message-time">
                {message.timestamp.toLocaleTimeString([], {
                  hour: '2-digit',
                  minute: '2-digit',
                })}
              </span>
            </div>
          </div>
        ))}

        {isLoading && (
          <div className="message message-bot">
            <div className="message-content">
              <span className="message-avatar">ü§ñ</span>
              <div className="typing-indicator">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
          </div>
        )}

        {error && (
          <div className="error-message">
            <strong>Error:</strong> {error}
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {isEscalated && (
        <div className="escalation-notice">
          <p>
            üîî Your chat has been escalated to {escalationType || 'support'}. They will be with you
            shortly.
          </p>
        </div>
      )}

      {!isEscalated && (
        <>
          {quickActions.length > 0 && (
            <div className="quick-actions">
              {quickActions.map((action) => (
                <button
                  key={action.id}
                  className="quick-action-btn"
                  onClick={() => handleQuickAction(action.action)}
                  disabled={isLoading}
                >
                  {action.label}
                </button>
              ))}
            </div>
          )}

          <form onSubmit={handleSendMessage} className="chatbot-input-form">
            <input
              type="text"
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              placeholder="Type your question..."
              disabled={isLoading || isEscalated}
              className="chatbot-input"
            />
            <button
              type="submit"
              disabled={isLoading || !inputText.trim() || isEscalated}
              className="send-btn"
            >
              Send
            </button>
          </form>
        </>
      )}
    </div>
  );
};

export default Chatbot;
