import React, { useEffect, useRef } from 'react';
import { useChatController } from '../chat_controller/useChatController';
import { MessageBubble, TypingIndicator, ChatInput } from '../widgets/ChatWidgets';
import './chat_screen.css';

interface ChatScreenProps {
    userId: string;
    onClose: () => void;
}

const ChatScreen: React.FC<ChatScreenProps> = ({ userId, onClose }) => {
    const { messages, isLoading, error, sendMessage } = useChatController(userId);
    const messagesEndRef = useRef<HTMLDivElement>(null);

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [messages, isLoading]); // Scroll on new messages or loading state change

    return (
        <div className="chat-screen-container">
            {/* Header */}
            <header className="chat-header">
                <div className="header-left">
                    <button className="back-btn" onClick={onClose} aria-label="Close Chat">
                        âœ•
                    </button>
                    <div className="bot-info">
                        <h1 className="bot-name">D2D Assistant</h1>
                        <span className="bot-status">
                            <span className="online-indicator"></span> Online
                        </span>
                    </div>
                </div>
                <div className="header-right">
                    {/* Add more header actions if needed */}
                </div>
            </header>

            {/* Messages Area */}
            <div className="chat-messages-area">
                {messages.length === 0 && (
                    <div className="empty-state">
                        <p>ðŸ‘‹ Hi there! How can I help you with your flight today?</p>
                    </div>
                )}

                {messages.map((msg) => (
                    <MessageBubble key={msg.id} message={msg} />
                ))}

                {isLoading && <TypingIndicator />}

                {error && <div className="error-banner">{error}</div>}

                <div ref={messagesEndRef} />
            </div>

            {/* Input Area */}
            <footer className="chat-footer">
                <ChatInput onSend={sendMessage} disabled={isLoading} />
            </footer>
        </div>
    );
};

export default ChatScreen;
